 data-msg-required="Bu alan boş bırakılamaz." data-msg-birthday="Geçersiz doğum tarihi." class="form-control required birthday-mask" data-placeholder="gg/aa/yyyy"  placeholder="gg/aa/yyyy" maxlength="10" value=""></div></div> <div class="col-24" style="display: none;">
	
<script>	
												// Doğum tarihi girişi için maske uygulama
document.addEventListener('DOMContentLoaded', function() {
    const birthdayInput = document.getElementById('birthdayFull');

    // Tarih maskesi
    birthdayInput.addEventListener('input', function() {
        this.value = this.value.replace(/[^0-9]/g, ''); // Sadece rakamları tut
        if (this.value.length > 2) {
            this.value = this.value.slice(0, 2) + '/' + this.value.slice(2); // gg/aa formatı
        }
        if (this.value.length > 5) {
            this.value = this.value.slice(0, 5) + '/' + this.value.slice(5); // gg/aa/yyyy formatı
        }
        if (this.value.length > 10) {
            this.value = this.value.slice(0, 10); // Maksimum 10 karakter
        }
    });

    // Form gönderiminde doğum tarihi kontrolü
    const form = document.querySelector('form');
    if (form) {
        form.addEventListener('submit', function(event) {
            const birthdayValue = birthdayInput.value;
            const dateParts = birthdayValue.split('/');

            // Geçerli bir tarih kontrolü
            if (dateParts.length === 3) {
                const day = parseInt(dateParts[0], 10);
                const month = parseInt(dateParts[1], 10) - 1; // Aylar 0'dan başlar
                const year = parseInt(dateParts[2], 10);
                const date = new Date(year, month, day);

                // Tarih geçerli mi?
                if (date.getFullYear() !== year || date.getMonth() !== month || date.getDate() !== day) {
                    alert('Geçersiz doğum tarihi. Lütfen kontrol edin.');
                    event.preventDefault(); // Formu gönderme
                }
            } else {
                alert('Doğum tarihi formatı geçersiz. Lütfen gg/aa/yyyy formatında girin.');
                event.preventDefault(); // Formu gönderme
            }
        });
    }
});
		</script>										
												
												
												
												
												
												
